use hex_literal::hex;
use rusb::{Context, Device, DeviceHandle, Result, UsbContext};
use std::time::Duration;

// device uid pid are picked directly form `lsusb` result
const VID: u16 = 0x04b4;
const PID: u16 = 0x8613;
const ONE_SECOND: Duration = Duration::from_secs(1);

pub fn init() -> Result<(Device<impl UsbContext>, DeviceHandle<impl UsbContext>)>
{
    let mut context = Context::new()?;
    let (device, mut handle) =
        open_device(&mut context, VID, PID).expect("Failed to open USB device");

    handle.reset()?;

    initialize_device(&mut handle)?;

    // We reopen because the old handle doesn't reflect reality. libusb bug? Usage issue?
    // Probably just a general misunderstanding about how long handles are valid.
    Ok((device, handle))
}

fn open_device<T: UsbContext>(
    context: &mut T,
    vid: u16,
    pid: u16,
) -> Option<(Device<T>, DeviceHandle<T>)> {
    let devices = match context.devices() {
        Ok(d) => d,
        Err(_) => return None,
    };

    for device in devices.iter() {
        let device_desc = match device.device_descriptor() {
            Ok(d) => d,
            Err(_) => continue,
        };

        if device_desc.vendor_id() == vid && device_desc.product_id() == pid {
            match device.open() {
                Ok(handle) => return Some((device, handle)),
                Err(_) => continue,
            }
        }
    }

    None
}

pub struct Chunk {
    pub bytes: [u8; 64],
    pub len: usize,
}

pub fn poke<T: UsbContext>(
    handle: &mut DeviceHandle<T>,
    bytes: &[u8],
) -> Result<Chunk> {
    handle.write_bulk(0x1, bytes, ONE_SECOND).unwrap();
    let mut buf = [0; 64];
    let bytes_read = handle.read_bulk(0x81, &mut buf, ONE_SECOND)?;

    Ok(Chunk {
        bytes: buf,
        len: bytes_read,
    })
}

pub fn firmware_version<T: UsbContext>(
    handle: &mut DeviceHandle<T>,
) -> Result<Chunk> {
    handle.write_bulk(0x1, &[2, 0], ONE_SECOND).unwrap();
    let mut buf = [0; 64];
    let bytes_read = handle.read_bulk(0x81, &mut buf, ONE_SECOND)?;

    Ok(Chunk {
        bytes: buf,
        len: bytes_read,
    })
}

pub fn serial_number<T: UsbContext>(
    handle: &mut DeviceHandle<T>,
) -> Result<Chunk> {
    handle.write_bulk(0x1, &[0x18, 0], ONE_SECOND)?;
    let mut buf = [0; 64];
    let bytes_read = handle.read_bulk(0x81, &mut buf, ONE_SECOND)?;

    Ok(Chunk {
        bytes: buf,
        len: bytes_read,
    })
}

// Only tested with a 2532
pub fn read<T: UsbContext>(handle: &mut DeviceHandle<T>) -> Result<Chunk> {
    let mut buf = [0; 64];

    handle.write_bulk(0x1, &[0x15], ONE_SECOND)?;
    handle.write_bulk(0x1, &[0x16], ONE_SECOND)?;
    handle.write_bulk(0x1, &[0x08, 0x00], ONE_SECOND)?;
    handle.write_bulk(0x1, &hex!("040000200000000040"), ONE_SECOND)?;
    let bytes_read = handle.read_bulk(0x81, &mut buf, ONE_SECOND)?;

    Ok(Chunk {
        bytes: buf,
        len: bytes_read,
    })
}

